import React from 'react';
import CardItem from './CardItem';

function RandomNumber() {
  return Math.floor(Math.random() * 12);
};

function NextCard() {

  const cardNumber = RandomNumber;

  const cardNumbers = [
    {
      id: '0',
      number: 'Hi',
      details: 'Welcome to my app, click the button to start the game and draw your first card!'
    },
    {
      id: '1',
      number: '1',
      details: 'test'
    },
    {
      id: '2',
      number: '2',
      details: 'test'
    },
    {
      id: '3',
      number: '3',
      details: 'test'
    },
    {
      id: '4',
      number: '4',
      details: 'test'
    },
    {
      id: '5',
      number: '5',
      details: 'test'
    },
    {
      id: '6',
      number: '6',
      details: 'test'
    },
    {
      id: '7',
      number: '7',
      details: 'test'
    },
    {
      id: '8',
      number: '8',
      details: 'test'
    },
    {
      id: '9',
      number: '9',
      details: 'test'
    },
    {
      id: '10',
      number: '10',
      details: 'test'
    },
    {
      id: '11',
      number: '11',
      details: 'test'
    },
    {
      id: '12',
      number: '12',
      details: 'test'
    }
  ];

  return (
    <CardItem
      number={cardNumbers.id(cardNumber).number}
      details={cardNumbers.id(cardNumber).details}
    />
  )
}

export default NextCard;
