import React, { useState } from 'react';
import './NextCard.css'

function NextCard() {

  const cardNumbers = [
    {
      id: '0',
      number: 'Hi',
      details: 'Welcome to my app, click the button to start the game and draw your first card!'
    },
    {
      id: '1',
      number: '1',
      details: 'MUST EITHER START A MAN OUT OR MOVE ONE MAN FORWARD 1 SQUARE'
    },
    {
      id: '2',
      number: '2',
      details: 'MUST EITHER START A MAN OUT OR MOVE ONE MAN FORWARD 2 SQUARES. DRAW AGAIN'
    },
    {
      id: '3',
      number: '3',
      details: 'MUST MOVE ONE MAN FORWARD 3 SQUARES'
    },
    {
      id: '4',
      number: '4',
      details: 'MUST MOVE ONE MAN BACKWARD 4 SQUARES'
    },
    {
      id: '5',
      number: '5',
      details: 'MUST MOVE ONE MAN FORWARD 5 SQUARES'
    },
    {
      id: '6',
      number: '6',
      details: 'Card 6'
    },
    {
      id: '7',
      number: '7',
      details: 'Card 7'
    },
    {
      id: '8',
      number: '8',
      details: 'Card 8'
    },
    {
      id: '9',
      number: '9',
      details: 'Card 9'
    },
    {
      id: '10',
      number: '10',
      details: 'Card 10'
    },
    {
      id: '11',
      number: '11',
      details: 'Card 11'
    },
    {
      id: '12',
      number: '12',
      details: 'Card 12'
    }
  ];

  const [card, setCard] = useState(cardNumbers[0]);

  const handleClick = () => {
    const updatedCard = cardNumbers[Math.floor(Math.random() * 12) + 1];
    setCard(updatedCard);
  }

  return (
    <>
      <div className='wrapper'>
        <h2>{card.number}</h2>
        <div className='box'>
          <p>{card.details}</p>
          <button onClick={handleClick} className='button'>Next Card</button>
        </div>
      </div>
    </>
  )
}

export default NextCard;
