import React from 'react';
import NextCard from './NextCard_copy';
import './CardItem.css';


function CardItem(props) {
  return (
    <>
      <div className='wrapper'>
        <h2>{props.number}</h2>
        <div className='box'>
          <p>{props.details}</p>
          <button onClick={() => NextCard} className='button'>Next Card</button>
        </div>
      </div>
    </>
  )
}

export default CardItem;
